package algs

import (
	"reflect"
	"testing"
)

func TestFindSubstring(t *testing.T) {
	for _, c := range []struct {
		s      string
		words  []string
		expect []int
	}{
		{"barfoothefoobarman", []string{"foo", "bar"}, []int{0, 9}},
		{"barfoothefoobarman", nil, nil},
		{"wordgoodgoodgoodbestword", []string{"word", "good", "best", "good"}, []int{8}},
		{
			"",
			[]string{"dhvf", "sind", "ffsl", "yekr", "zwzq", "kpeo", "cila", "tfty", "modg", "ztjg", "ybty", "heqg", "cpwo", "gdcj", "lnle", "sefg", "vimw", "bxcb"},
			[]int{935},
		},
		{"abababab", []string{"a", "b", "a"}, []int{0, 2, 4}},
	} {
		got := FindSubstring(c.s, c.words)
		if !reflect.DeepEqual(got, c.expect) {
			t.Errorf("s: %q\t|\twords: %v\t|\texpect: %v\t|\tgot: %v", c.s, c.words, c.expect, got)
		}
	}
}
